import React, { useCallback, useReducer } from 'react';

import Input from'../../shared/components/FormElements/Input';
import { 
  VALIDATOR_REQUIRE, 
  VALIDATOR_MINLENGTH 
} from '../../shared/util/validators';
import './NewPlace.css';

const formReduser = (state, action) => {
  switch (action.type) {
    case 'IMPUT CHANGE':
      let formIsValed = true;
      for (const inputId in state.inputs) {
        if (inputId === action.inputId) {
          formIsValed = formIsValed && action.isValid;
        } else {
          formIsValed = formIsValed && state.inputs[inputId].isValid;
        }
      }
      return {
        ...state,
        inputs: {
          ...state.inputs,
          [action.inputId]: { value: action.value, isValid: action.isValid }
        }
      };
    default:
      return state;
  }

};

const NewPlace = () => {
  useReducer(formReduser, {
    inputs: {
      title: {
        value: '',
        isValid: false
      },
      description: {
        value: '',
        isValid: false
      }
    },
    isValid: false
  });

  const titleInputHandler = useCallback((id, value, isValid) => {}, []);

  const descriptionInputHandler = useCallback((id, value, isValid) => {}, []); 

  return (
    <form className="place-form" >
      <Input 
        id="title"
        element="input" 
        type="text" 
        label="Title" 
        validators={[VALIDATOR_REQUIRE()]}
        errorText="Plaese enter a valid title."
        onInput={titleInputHandler}
      />
      <Input 
        id="description"
        element="textarea" 
        label="Description" 
        validators={[VALIDATOR_MINLENGTH(5)]}
        errorText="Plaese enter a valid description (at least 5 characters)."
        onInput={descriptionInputHandler}
      />
    </form>
  );
};

export default NewPlace;
